DROP DATABASE IF EXISTS foods;
CREATE DATABASE foods;
USE foods;

CREATE TABLE users (
    id INT PRIMARY KEY AUTO_INCREMENT,
    name VARCHAR(225),
    email VARCHAR(225),
    password VARCHAR(225)
);

CREATE TABLE recipies (
    id INT PRIMARY KEY AUTO_INCREMENT,
    name VARCHAR(225),
    image VARCHAR(225),
    ingredients VARCHAR(500),
    making_procedure VARCHAR(500),
    video VARCHAR(225)
);

